import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { DocumentEditor } from "@onlyoffice/document-editor-react";

export default function EditorPage() {
  const { projectId } = useParams();
  const navigate = useNavigate();
  const token = localStorage.getItem('token');
  const [docConfig, setDocConfig] = useState(null);

  useEffect(() => {
    // If not logged in, go to home
    if (!token) {
      navigate('/');
      return;
    }

    // Fetch the COMPLETE config from Flask
    fetch(`http://localhost:5001/projects/${projectId}/document-config?token=${token}`)
      .then((res) => res.json())
      .then((data) => {
        if (data.status === 'error') {
          alert(data.message);
          navigate('/projects');
        } else {
          // data SHOULD have:
          // {
          //   "documentType": "word",
          //   "document": {...},
          //   "editorConfig": {...},
          //   "token": "JWT_STRING"
          // }
          console.log("Received config:", data);
          setDocConfig(data);
        }
      })
      .catch((error) => {
        console.error("Error loading doc config:", error);
        alert("Failed to load doc config");
      });
  }, [projectId, token, navigate]);

  const handleDocumentReady = () => {
    console.log("Document loaded in ONLYOFFICE editor");
  };

  const handleLoadComponentError = (errorCode, errorDescription) => {
    console.error("Load component error:", errorCode, errorDescription);
    alert(`ONLYOFFICE load error: ${errorDescription}`);
  };

  return (
    <div style={{ margin: '20px' }}>
      <h2>Editor Page: {projectId}</h2>
      <div style={{ width: '100%', height: '80vh' }}>
        {docConfig ? (
          <DocumentEditor
            id="docEditor"
            documentServerUrl="http://localhost" // Because container is mapped to port 80
            config={docConfig}
            events_onDocumentReady={handleDocumentReady}
            onLoadComponentError={handleLoadComponentError}
            width="100%"
            height="100%"
          />
        ) : (
          <p>Loading document config...</p>
        )}
      </div>
    </div>
  );
}
