import React, { useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

function EditorPage() {
  const { projectId } = useParams();
  const navigate = useNavigate();
  const token = localStorage.getItem('token');

  useEffect(() => {
    if (!token) {
      navigate('/');
      return;
    }

    // 1) Dynamically load script
    const script = document.createElement('script');
    script.src = "http://localhost:80/web-apps/apps/api/documents/api.js";
    script.onload = () => {
        console.log("ONLYOFFICE script loaded!");
        if (window.DocEditor) {
        new window.DocEditor('editorContainer', config);
        } else {
        console.error("DocEditor still not found after script load");
        }
    };
    script.onerror = () => console.error("Failed to load ONLYOFFICE script");
    document.body.appendChild(script);

    // 1) Fetch ONLYOFFICE config from Flask
    fetch(`http://localhost:5001/projects/${projectId}/document-config?token=${token}`)
      .then(res => res.json())
      .then(config => {
        if (config.status === 'error') {
          alert(config.message);
          navigate('/projects');
        } else {
          // 2) Initialize the ONLYOFFICE editor
          if (window.DocEditor) {
            new window.DocEditor('editorContainer', config);
          } else {
            console.error("ONLYOFFICE script not loaded or DocEditor not found");
          }
        }
      })
      .catch(err => {
        console.error("Error fetching document config:", err);
      });
  }, [projectId, token, navigate]);

  return (
    <div style={{ margin: '20px' }}>
      <h2>Editor Page: {projectId}</h2>
      {/* The ONLYOFFICE editor will render inside this div */}
      <div
        id="editorContainer"
        style={{ width: '100%', height: '80vh', border: '1px solid #ccc' }}
      />
    </div>
  );
}

export default EditorPage;
