import React, { useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

function EditorPage() {
  const { projectId } = useParams();
  const navigate = useNavigate();
  const token = localStorage.getItem('token');

  useEffect(() => {
    if (!token) {
      navigate('/');
      return;
    }

    // Function to load ONLYOFFICE script dynamically
    const loadOnlyOfficeScript = () => {
      return new Promise((resolve, reject) => {
        if (window.DocEditor) {
          resolve();
          return;
        }
        const script = document.createElement('script');
        script.src = 'http://localhost:80/web-apps/apps/api/documents/api.js';
        script.onload = () => {
          console.log("ONLYOFFICE script loaded!");
          let attempts = 0;

          // Retry checking if DocEditor is available
          const checkInterval = setInterval(() => {
            if (window.DocEditor) {
              clearInterval(checkInterval);
              resolve();
            }
            attempts++;
            if (attempts > 10) {
              clearInterval(checkInterval);
              reject("DocEditor not available after multiple attempts.");
            }
          }, 500);
        };
        script.onerror = () => reject("Failed to load ONLYOFFICE script");
        document.body.appendChild(script);
      });
    };

    // Fetch document config and load script
    fetch(`http://localhost:5001/projects/${projectId}/document-config?token=${token}`)
      .then((res) => res.json())
      .then((data) => {
        if (data.status === 'error') {
          alert(data.message);
          navigate('/projects');
        } else {
          loadOnlyOfficeScript()
            .then(() => {
              console.log("DocEditor found, initializing editor...");
              new window.DocEditor('editorContainer', data);
            })
            .catch((err) => console.error(err));
        }
      })
      .catch((error) => {
        console.error("Error loading document config:", error);
      });

  }, [projectId, token, navigate]);

  return (
    <div>
      <h2>Editor Page: {projectId}</h2>
      <div id="editorContainer" style={{ width: '100%', height: '80vh', border: '1px solid #ccc' }} />
    </div>
  );
}

export default EditorPage;
